    private function  dropForeignKeys(){

        // drop foreign key, so that tables can be truncated
        Schema::table('countries', function (Blueprint $table) {
            $table->dropForeign('countries_currency_id_foreign');
        });
        Schema::table('accounts', function (Blueprint $table) {
            $table->dropForeign('accounts_country_id_foreign');
        });
        Schema::table('addressable', function (Blueprint $table) {
            $table->dropForeign('addressable_country_id_foreign');
        });
        Schema::table('states', function (Blueprint $table) {
            $table->dropForeign('states_country_id_foreign');
        });

    }

    private function  reCreateForeignKeys(){

        // re-Create foreign keys
        Schema::table('countries', function (Blueprint $table) {
            $table->foreign('currency_id')->references('id')->on('currencies');
        });
        Schema::table('accounts', function (Blueprint $table) {
            $table->foreign('country_id')->references('id')->on('countries');
        });
        Schema::table('addressable', function (Blueprint $table) {
            $table->foreign('country_id')->references('id')->on('countries');
        });
        Schema::table('states', function (Blueprint $table) {
            $table->foreign('country_id')->references('id')->on('countries');
        });


    }
